export const $=(s,c=document)=>c.querySelector(s);export const $$=(s,c=document)=>Array.from(c.querySelectorAll(s));export const html=(a,...v)=>a.reduce((x,s,i)=>x+s+(v[i]??''),'');let tt;export function showToast(m,t=2500){let e=document.getElementById('app-toast');if(!e){e=document.createElement('div');e.id='app-toast';e.className='toast';document.body.appendChild(e);}e.textContent=m;e.classList.add('show');clearTimeout(tt);tt=setTimeout(()=>e.classList.remove('show'),t);}export function focusApp(){const a=document.getElementById('app');a&&a.focus();}