export const onlyDigits=v=>(v||'').replace(/\D+/g,'');export function isValidCPF(c){c=onlyDigits(c);if(!c||c.length!==11||/^([0-9])\1{10}$/.test(c))return false;let s=0;for(let i=0;i<9;i++)s+=parseInt(c[i])*(10-i);let d1=11-(s%11);if(d1>=10)d1=0;s=0;for(let i=0;i<10;i++)s+=parseInt(c[i])*(11-i);let d2=11-(s%11);if(d2>=10)d2=0;return d1===parseInt(c[9])&&d2===parseInt(c[10]);}export function isValidPhoneBR(v){const d=onlyDigits(v);return d.length===10||d.length===11;}export function isValidCEP(v){return/^\d{5}-?\d{3}$/.test(v||'');}export function isAdult14(ds){if(!ds)return false;const d=new Date(ds);if(Number.isNaN(d.getTime()))return false;const t=new Date();let a=t.getFullYear()-d.getFullYear();const m=t.getMonth()-d.getMonth();if(m<0||(m===0&&t.getDate()<d.getDate()))a--;return a>=14;}