import{html,$}from'../core/dom.min.js';import{attachFormValidation}from'../features/form.min.js';export default function cadastro(){const t=html`<section class='container'><header><h1>Cadastro</h1><p>Preencha para participar ou receber novidades.</p><small class='muted'>Campos marcados com * são obrigatórios.</small></header><form id='cadForm' class='mt-16' action='#' method='post' novalidate><p class='help'>Validamos CPF, telefone, CEP e idade mínima (14 anos).</p><fieldset><legend>Dados Pessoais</legend><label for='nome'>Nome Completo *</label><input id='nome' name='nome' type='text' required autocomplete='name' placeholder='Ex.: Maria Silva'><label for='email'>E-mail *</label><input id='email' name='email' type='email' required autocomplete='email' placeholder='nome@exemplo.com'><label for='cpf'>CPF *</label><input id='cpf' name='cpf' type='text' inputmode='numeric' maxlength='14' required placeholder='000.000.000-00'><label for='telefone'>Telefone *</label><input id='telefone' name='telefone' type='tel' inputmode='tel' maxlength='15' required placeholder='(00) 00000-0000'><label for='nascimento'>Data de Nascimento *</label><input id='nascimento' name='nascimento' type='date' required></fieldset><fieldset><legend>Endereço</legend><label for='logradouro'>Logradouro *</label><input id='logradouro' name='logradouro' type='text' required autocomplete='address-line1'><label for='numero'>Número *</label><input id='numero' name='numero' type='text' required autocomplete='address-line2'><label for='bairro'>Bairro *</label><input id='bairro' name='bairro' type='text' required autocomplete='address-level3'><label for='cep'>CEP *</label><input id='cep' name='cep' type='text' inputmode='numeric' maxlength='9' required placeholder='00000-000'><label for='cidade'>Cidade *</label><input id='cidade' name='cidade' type='text' required autocomplete='address-level2'><label for='estado'>Estado (UF) *</label><select id='estado' name='estado' required autocomplete='address-level1'><option value='' selected disabled>Selecione</option><option>AC</option><option>AL</option><option>AP</option><option>AM</option><option>BA</option><option>CE</option><option>DF</option><option>ES</option><option>GO</option><option>MA</option><option>MT</option><option>MS</option><option>MG</option><option>PA</option><option>PB</option><option>PR</option><option>PE</option><option>PI</option><option>RJ</option><option>RN</option><option>RS</option><option>RO</option><option>RR</option><option>SC</option><option>SP</option><option>SE</option><option>TO</option></select></fieldset><fieldset><legend>Interesse</legend><label for='interesse'>Área de interesse</label><select id='interesse' name='interesse'><option>Voluntariado</option><option>Doações</option><option>Receber newsletter</option></select><label><input type='checkbox' name='termos' required> Li e concordo com os termos *</label></fieldset><div class='form-actions'><button class='btn' type='submit'>Enviar</button><button type='reset'>Limpar</button></div></form></section>`;function afterRender(){attachFormValidation($('#cadForm'));}return{template:t,afterRender};}