import{html}from'../core/dom.js';export default function home(){const t=html`<section class='hero'><div class='container'><div class='row'><div class='span-12 lg-span-7'><h1>Missão, Visão e Valores</h1><p>Somos a VivaMais, ONG dedicada à educação e saúde comunitária.</p><p><span class='badge'>Visão</span> Impacto social medido e compartilhado.</p><p><a class='btn' href='#/cadastro'>Quero participar</a></p></div><div class='span-12 lg-span-5 overlay'><picture><source srcset='assets/img/hero-1200.webp 1200w, assets/img/hero-600.webp 600w' type='image/webp'><source srcset='assets/img/hero-1200.jpg 1200w, assets/img/hero-600.jpg 600w' type='image/jpeg'><img src='assets/img/hero-600.jpg' srcset='assets/img/hero-600.jpg 600w, assets/img/hero-1200.jpg 1200w' sizes='(max-width: 768px) 600px, 1200px' width='1200' height='600' alt='Ações comunitárias da ONG VivaMais' loading='eager' decoding='async'></picture><div class='overlay-text'>Impacto que você pode ver</div></div></div></div></section><section class='container'><h2>Histórico e Conquistas</h2><div class='row'><article class='card span-12 md-span-4'><h3>+5 mil atendimentos</h3><p>Saúde preventiva desde 2022.</p></article><article class='card span-12 md-span-4'><h3>Parcerias locais</h3><p>Rede com escolas e postos de saúde.</p></article><article class='card span-12 md-span-4'><h3>Relatórios anuais</h3><p>Resultados e prestação de contas.</p></article></div></section>`;return{template:t};}